package operators;

public class LogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		int x = 6;
		int y = 3;
		if ((x == 1) && (y == 2)) {
		System.out.println("x is 1 AND y is 2");
		}
		
		if ((x == 1) || (y == 2)) {
		System.out.println("x is 1 OR y is 2");
		}
		
	}

}
